package pattern;

public class pattern15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=5;
		int col=5;
		for(int i=1;i<=row;i++)//for rows
		{
			for(int j=1;j<=col;j++)//for columns
			{
				System.out.print(i);
				
			}
			col--;
			System.out.println();
	}
	}

}
