package pattern;

public class pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=5;
		int col=1;
		for(int i=1;i<=row;i++)//for rows
		{
			for(int j=0;j<col;j++)//for columns
			{
				System.out.print(i);
				
			}
			col++;
			System.out.println();
	}

	}

}
